static enum lx_bnf_token
z4(struct lx_bnf_lx *lx)
{
	int c;

	enum {
		S1, S2, S3, S4, S5, S6, S7, S8, S9, S10, 
		S11, S12
	} state;

	assert(lx != NULL);

	if (lx->clear != NULL) {
		lx->clear(lx);
	}

	state = S12;

	lx->start = lx->end;

	while (c = lx_getc(lx), c != EOF) {
		switch (state) {
		case S6:
		case S10:
			break;

		default:
			if (lx->push != NULL) {
				if (-1 == lx->push(lx, c)) {
					return TOK_ERROR;
				}
			}
			break;

		}

		switch (state) {
		case S1: /* e.g. "::=" */
			switch (c) {
			default:  lx_bnf_ungetc(lx, c); return TOK_EQUALS;
			}

		case S2: /* e.g. "::" */
			switch (c) {
			case '=': state = S1;      continue;
			default:  lx->lgetc = NULL; return TOK_UNKNOWN;
			}

		case S3: /* e.g. "''" */
			switch (c) {
			default:  lx_bnf_ungetc(lx, c); return TOK_EMPTY;
			}

		case S4: /* e.g. "\x0a\x0a" */
			switch (c) {
			default:  lx_bnf_ungetc(lx, c); return TOK_SEP;
			}

		case S5: /* e.g. "\x0a" */
			switch (c) {
			case '\n': state = S4;      continue;
			default:  lx_bnf_ungetc(lx, c); return lx->z(lx);
			}

		case S6: /* e.g. "\x09" */
			switch (c) {
			case '\t':	          continue;
			case '\v':	          continue;
			case '\f':	          continue;
			case '\r':	          continue;
			case ' ':	          continue;
			default:  lx_bnf_ungetc(lx, c); return lx->z(lx);
			}

		case S7: /* e.g. "\"" */
			switch (c) {
			case '\"': state = S3;      continue;
			default:  lx_bnf_ungetc(lx, c); return lx->z = z1, lx->z(lx);
			}

		case S8: /* e.g. "'" */
			switch (c) {
			case '\'': state = S3;      continue;
			default:  lx_bnf_ungetc(lx, c); return lx->z = z2, lx->z(lx);
			}

		case S9: /* e.g. ":" */
			switch (c) {
			case ':': state = S2;      continue;
			default:  lx->lgetc = NULL; return TOK_UNKNOWN;
			}

		case S10: /* e.g. "<" */
			switch (c) {
			default:  lx_bnf_ungetc(lx, c); return lx->z = z3, lx->z(lx);
			}

		case S11: /* e.g. "|" */
			switch (c) {
			default:  lx_bnf_ungetc(lx, c); return TOK_ALT;
			}

		case S12: /* start */
			switch (c) {
			case '\t': state = S6;      continue;
			case '\n': state = S5;      continue;
			case '\v': state = S6;      continue;
			case '\f': state = S6;      continue;
			case '\r': state = S6;      continue;
			case ' ': state = S6;      continue;
			case '\"': state = S7;      continue;
			case '\'': state = S8;      continue;
			case ':': state = S9;      continue;
			case '<': state = S10;      continue;
			case '|': state = S11;      continue;
			default:  lx->lgetc = NULL; return TOK_UNKNOWN;
			}
		}
	}

	lx->lgetc = NULL;

	switch (state) {
	case S1: return TOK_EQUALS;
	case S3: return TOK_EMPTY;
	case S4: return TOK_SEP;
	case S5: return TOK_EOF;
	case S6: return TOK_EOF;
	case S7: return TOK_EOF;
	case S8: return TOK_EOF;
	case S10: return TOK_EOF;
	case S11: return TOK_ALT;
	default: errno = EINVAL; return TOK_ERROR;
	}
}

const char *
lx_bnf_name(enum lx_bnf_token t)
{
	switch (t) {
	case TOK_EMPTY: return "EMPTY";
	case TOK_LITERAL: return "LITERAL";
	case TOK_NAME: return "NAME";
	case TOK_CHAR: return "CHAR";
	case TOK_SEP: return "SEP";
	case TOK_ALT: return "ALT";
	case TOK_EQUALS: return "EQUALS";
	case TOK_EOF:     return "EOF";
	case TOK_ERROR:   return "ERROR";
	case TOK_UNKNOWN: return "UNKNOWN";
	default: return "?";
	}
}

const char *
lx_bnf_example(enum lx_bnf_token (*z)(struct lx_bnf_lx *), enum lx_bnf_token t)
{
	assert(z != NULL);

	if (z == z1) {
		switch (t) {
		case TOK_LITERAL: return "\"";
		case TOK_CHAR: return "a";
		default: goto error;
		}
	} else
	if (z == z2) {
		switch (t) {
		case TOK_LITERAL: return "'";
		case TOK_CHAR: return "a";
		default: goto error;
		}
	} else
	if (z == z3) {
		switch (t) {
		case TOK_NAME: return ">";
		case TOK_CHAR: return "a";
		default: goto error;
		}
	} else
	if (z == z4) {
		switch (t) {
		case TOK_EMPTY: return "''";
		case TOK_SEP: return "\x0a\x0a";
		case TOK_ALT: return "|";
		case TOK_EQUALS: return "::=";
		default: goto error;
		}
	}

error:

	errno = EINVAL;
	return NULL;
}

void
lx_bnf_init(struct lx_bnf_lx *lx)
{
	static const struct lx_bnf_lx lx_default;

	assert(lx != NULL);

	*lx = lx_default;

	lx->c = EOF;
	lx->z = NULL;

	lx->end.byte = 0;
	lx->end.line = 1;
	lx->end.col  = 1;
}

enum lx_bnf_token
lx_bnf_next(struct lx_bnf_lx *lx)
{
	enum lx_bnf_token t;

	assert(lx != NULL);

	if (lx->lgetc == NULL) {
		return TOK_EOF;
	}

	if (lx->z == NULL) {
		lx->z = z4;
	}

	t = lx->z(lx);

	if (lx->push != NULL) {
		if (-1 == lx->push(lx, '\0')) {
			return TOK_ERROR;
		}
	}

	if (lx->lgetc == NULL && lx->free != NULL) {
		lx->free(lx);
	}

	return t;
}

